void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = texture(iChannel0, uv);

    vec3 newCol = vec3(0.0);

    for (int i = 0; i < 3; i++) {

        float modif1 = 1.0 - texture(iChannel1, uv - vec2(iTime)).x;
        float modif2 = texture(iChannel1, uv + vec2(iTime)).x;

        float range = mod(iTime + modif1, 2.0) - 0.5;
        float range2 = mod(iTime + modif2 + 0.5, 2.0) - 0.5;

    	float origColor = col[i];

        newCol[i] = min(abs(origColor - range), abs(origColor - range2));
    }

    fragColor = vec4(newCol, 1.0);
}
