// Authors: Benjamin Kahl, Jaroslaw Urbanek

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Set r position in relation to mouse coord
    float r = 100.0 + abs(800.0*sin(0.3*iTime));

    // Set origin to center
    vec2 uv = fragCoord - 0.5*iResolution.xy;

    // Compute color value
    r*=0.1;
    float value = mod((uv.x*uv.x + uv.y*uv.y), r*r) / (r*r);

    // Output to screen
    fragColor = vec4(vec3(value),1.0);
}
