// by nikos papadopoulos, 4rknova / 2013
// WTFPL

// Notes
// p: Screen coordinates in [-a,a]x[-1,1] space (aspect corrected).
// t: The texture uv coordinates.
//	  u: The angle between the positive x axis and p.
//	  v: The inverse distance of p from the axis origin.
// s: Scrolling offset to create the illusion of movement.
// z: Texture uv scale factor.
// m: Brightness scale factor.

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

	vec2  p = (2. * fragCoord.xy / iResolution.xy - 1.) * vec2(iResolution.x / iResolution.y,1.);
	vec2  t = vec2(atan(p.x, p.y) / 3.1416, 1. / length(p));
    float pitch = texture(iChannel1, vec2(0.1, 0.0)).x / 500.0;

    vec2 s = iTime * vec2(-pitch, 0.14);

    vec2  z = vec2(2, 0.8);
	float m = t.y + 1.2;

    float timeSine = 5.0 - abs(mod(iTime, 10.0) - 5.0);
	fragColor = vec4(texture(iChannel0, t * z + s).xyz * (vec3(timeSine / 2.0 + 0.15, 1.0 - timeSine / 5.0, 1.0 - timeSine / 6.0)) / m, 1);
}
