float circle(vec2 p, float r)
{
	return length(p.xy) - r;
}

float rect( vec2 p, vec2 b )
{
  vec2 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

vec2 rot45(vec2 p)
{
	return (p + vec2(p.y, -p.x)) * sqrt(0.5);
}


float map(vec2 pos)
{
    const float thickness = 0.1834375;
    const float inner_radius = 1.0 - 2.0 * thickness;
    vec2 cpos = pos + vec2(1.25, 0);
    vec2 rpos = rot45(vec2(cpos.xy));
    float c = max(max(circle(rpos.xy, 1.0),
                     -circle(rpos.xy, inner_radius)),
                 -rect(rpos - vec2(1, -1), vec2(1, 1)));

    vec2 rpos2 = rot45(vec2(cpos.x - 1.15, abs(pos.y)));
    float r = rect(rpos2, vec2(thickness, 0.555));

    vec2 opos = pos - vec2(1.25, 0);
	float o = max(circle(opos.xy, 1.0),
                 -circle(opos.xy, inner_radius));

    float d = min(min(c, r), o);
    return d;
}

const vec3 gray = vec3(139, 139, 137) / 255.0;
const vec3 purple = vec3(94, 61, 204) / 255.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy / 2.);
    float zoom = iResolution.x * 0.2;
    uv /= zoom;

    float d = map(uv);
    vec3 color = uv.x + 1.0 < abs(uv.y) ? gray : purple;
    color *= color;

    float a = clamp(d * zoom, 0.0, 1.0);
    fragColor = vec4(mix(color, vec3(1.0), a), 1.0);
    fragColor.rgb = sqrt(fragColor.rgb);
}
