void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - vec2(0.5 * iResolution.x, 0.0)) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    float pi = 3.0 + texelFetch(iChannel0, ivec2(0, 0), 0).x;
    fragColor.rgb = digitIn(fragColor.rgb, 0.5 + 0.5 * vec3(sin(iTime), cos(iTime), sin(iTime * 2.0)), uv - vec2(-0.3, 0.5), 100.0, pi);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texelFetch(iChannel0, ivec2(0, 0), 0);
    if (iFrame % 10 == 0) {
        int n = iFrame / 10;
        fragColor.x += float(1 - 2 * (n % 2)) / float((n + 1) * (n + 2) * (2 * n + 3));
    }
}

