// t âˆˆ [0, 1]
vec4 flag(float p) {
	if (p < 1./5.) {
		return vec4(0.639, 0.08, 0.384, 1);
	} else if (p < 2./5.) {
		return vec4(0.827, 0.384, 0.643, 1);
	} else if (p < 3./5.) {
		return vec4(1);
    } else if (p < 4./5.) {
        return vec4(1., 0.604, 0.337, 1);
    } else {
        return vec4(0.835, 0.176, 0.0, 1);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = flag(uv.y);
}
