void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 uv = fragCoord/iResolution.xy;
  uv -= 0.5;

  float a = iTime / 15.0 + cos(iTime/3.0 + cos(iTime)/4.0)/7.0 + cos(length(uv) * 5.0 - iTime/3.0)/10.0;
  float s = sin(a);
  float c = cos(a);
  mat2 m = mat2(c, -s, s, c);



  uv = m * uv;


  float c1 =  cos(uv.x * 40.0 * (6.0 + cos(iTime/3.0 + 1.0)) / (2.0 + sin(uv.y*4.0+ iTime)/4.0) + iTime);
  float c2 =  cos((uv.y * 40.0 * (6.0 + cos(iTime/3.0)) + 20.0 * sin(uv.x/3.0 + iTime/4.0) * 3.0) / (2.0 + sin(uv.x*2.0+ iTime)/4.0) + iTime);

  fragColor.x = c1;
  fragColor.z = (1.0 - c1 + c2) / 2.0;
  fragColor.y = c2;

  fragColor = round(fragColor * 4.0) / 4.0;



}
