uniform vec2 u_resolution;
uniform float u_time;

vec2 u_position = vec2(10.);
float u_radius = 160.;

float variations = 10.;
float speed = 10.;


float random(vec2 st) { return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.153); }

float quitic(float x) { return x * x * x * (x * (x * 6. - 15.) + 10.); }
float hermite(float x) { return x * x * (3.0 - 2.0 * x); }

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = vec2(fragCoord.x, iResolution.y - fragCoord.y);

    float time_seed = floor(variations * fract(iTime * speed / variations)) / variations;

	vec3 black = vec3(0.);
    vec3 white = vec3(1.);
    vec3 red = vec3(1., 0, 0);
    vec3 green = vec3(0, 1., 0);
    vec3 blue = vec3(0, 0, 1.);

    vec3 color = vec3(0.);


    float dst = 0.0;
    dst = distance(st, u_position + u_radius);

    float w = dst / u_radius;
    w = 6. * -log(w);
    w = float(w > random(st + time_seed + 3.));

    float r = step(dst, u_radius) * float(random(st * 2. + time_seed) > 0.98);
    float g = step(dst, u_radius) * float(random(st * 3. + time_seed) > 0.98);
    float b = step(dst, u_radius) * float(random(st * 4. + time_seed) > 0.98);


    color += mix(black, white, step(1.0, w));

    color *= (1. - (float(bool(r) && bool(w))));
    color += mix(black, red, step(1.0, r));

    color *= (1. - (float(bool(g) && bool(w))));
    color *= (1. - (float(bool(g) && bool(r))));
    color += mix(black, green, step(1.0, g));

    color *= (1. - (float(bool(b) && bool(w))));
    color *= (1. - (float(bool(b) && bool(r))));
    color *= (1. - (float(bool(b) && bool(g))));
    color += mix(black, blue, step(1.0, b));



    fragColor = vec4(color, 1.);
}
