//Display the buffer
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define RADIUS  0.01
#define SAMPLES 3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iMouse.xy)/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float d = length(uv);

    float c = 0.0;

    if (d < 0.1) {
        c = 1.0;
    }else{
        c = 0.0;
    }

    vec3 pfragColor = vec3(0);

	for (int i = -SAMPLES; i < SAMPLES; i++) {
		for (int j = -SAMPLES; j < SAMPLES; j++) {
			pfragColor += texture(iChannel0, fragCoord.xy / iResolution.xy + vec2(i, j) * (RADIUS/float(SAMPLES))).xyz
				 / pow(float(SAMPLES) * 2., 2.);
		}
    }

    fragColor = vec4(pfragColor * vec3(0.8), 1.0) + vec4(vec3(c), 1.0);
}
