renderpass.code:
float heart( vec2 p )
{
    p *= 1.5;
    p.x = abs(p.x);
    p.y =  p.y*1.0 - p.x*(1.0-p.x);
    return length(p) - 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //
    const float maxValue = 1.5;
    vec2 P = 1.0 * (fragCoord.xy - iResolution.xy / 2.0) / min(iResolution.x, iResolution.y);
    P *= maxValue; 
    float d = heart(P);
	fragColor = vec4(1.0, 0.1, clamp(d / 0.01, 0.0, 1.0),1.0);

}
