#define S(a,b,t) smoothstep(a, b, t)

float xOffset(float y, float wave, float force) {
    float w = y*20. + iTime*50.;
    return (
        sin(w)
       	+ sin(w * 10.0) * 0.5
        + sin(w * 10.0) * 0.3
    ) * wave * 0.02 * force;
}

float waveF(float y, float speed, float loop, float size) {
	float s = abs(mod(y + iTime * speed, loop) - 0.5);
    return S(1., 0., s*size);
}

vec2 screenDistort(vec2 uv)
{
	uv -= vec2(.5,.5);
	uv = uv*1.2*(1./1.2+2.*uv.x*uv.x*uv.y*uv.y);
	uv += vec2(.5,.5);
	return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;

    	//Screen view
    uv = screenDistort(uv);

   		//Wave function
    float force0 = 0.05;
    float force1 = 0.4;
    float wave1 = waveF(uv.y,  0.4, 1.5, 15.);
    float force2 = 1.;
    float wave2 = waveF(uv.y,  0.7, 2.8, 20.);
    float force3 = 0.7;
    float wave3 = waveF(uv.y, 0.55, 2.8, 33.);

    	//Offsets
    uv.x += xOffset(uv.y, 1., 0.05);
    uv.x += xOffset(uv.y, wave1, force1);
    uv.x += xOffset(uv.y, wave2, force2);
    uv.x += xOffset(uv.y, wave3, force3);

    	//Texture
	fragColor = texture(iChannel0, uv);
	vec4 noise = texture(iChannel1, uv);
    noise = noise.xxxw;

    	//Adds noise
    fragColor = mix(fragColor, noise, 0.1);			//Base noise
    fragColor = mix(fragColor, noise, wave1*0.2);	//Wave noise
    fragColor = mix(fragColor, noise, wave2*0.2);
    fragColor = mix(fragColor, noise, wave3*0.2);
}
