void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = (I*2.-iResolution.xy)/iResolution.y/1.8;

	vec3 r0 = vec3(17,1,8);
    vec3 forward = normalize(vec3(3,17,3)-r0);
    vec3 right = normalize(cross(forward,vec3(0,0,1)));
    vec3 up = cross(right,forward);
    vec3 rd = normalize(forward + right*uv.x + up*uv.y);

    surface_intersection i = ray_scene(r0, rd);

    vec3 light = vec3(0);

	vec3 sun = vec3(0,cos(iTime),sin(iTime))*100.;
    float theta = sin(iTime/10.)/10.;
    sun.xz *= mat2(cos(theta),sin(theta),-sin(theta),cos(theta));

    vec3 lamp = vec3(10,3,12);

    vec3 ld_sun = normalize(sun-i.coord);
    surface_intersection l_sun = ray_scene(i.coord + ld_sun*1e-4, ld_sun);
    if(l_sun.type==1){
    	light += dot(i.normal,ld_sun);
    }


    vec3 ld_lamp = normalize(lamp-i.coord);
    surface_intersection l_lamp = ray_scene(i.coord + ld_lamp*1e-4, ld_lamp);
    if(l_lamp.type==2){
    	light += dot(i.normal,ld_lamp)/dot(lamp-i.coord,lamp-i.coord)*2e2*vec3(.3,.22,.1);
    }

    O = (i.type==1)?vec4(1):vec4( light, 1 );
}

struct surface_intersection {
    vec3 coord;
    vec3 normal;
    float d;
    int type;
};

struct AABB {
    vec3 a;
    vec3 b;
};

AABB[] walls = AABB[] (
	AABB(vec3(0,0,0),vec3(20,20,12)),
	AABB(vec3(0,3,0),vec3(3,20,12)),
	AABB(vec3(17,3,0),vec3(20,20,12)),
	AABB(vec3(8,10,0),vec3(10,12,12)),
	AABB(vec3(3,19,0),vec3(6,20,12)),
	AABB(vec3(9,19,0),vec3(11,20,12)),
	AABB(vec3(14,19,0),vec3(17,20,12)),
	AABB(vec3(6,19.4,0),vec3(9,20,3)),
	AABB(vec3(6,19.4,9),vec3(9,20,12)),
	AABB(vec3(11,19.4,0),vec3(14,20,3)),
	AABB(vec3(11,19.4,9),vec3(14,20,12))
);

AABB[] sunlights = AABB[] (
	AABB(vec3(6,19.9,3),vec3(9,20,9)),
	AABB(vec3(11,19.9,3),vec3(14,20,9))
);

AABB[] lamplights = AABB[] (
	AABB(vec3(9,2,11.9),vec3(11,4,12))
);


bool inAABB(AABB box, vec3 p){
    return (p.x>box.a.x && p.y>box.a.y && p.z>box.a.z && 
            p.x<box.b.x && p.y<box.b.y && p.z<box.b.z);
}

surface_intersection ray_AABB(vec3 r0, vec3 rd, AABB box){

    vec3 tMin = (box.a - r0) / rd;
    vec3 tMax = (box.b - r0) / rd;
    vec3 t1 = min(tMin, tMax);
    vec3 t2 = max(tMin, tMax);
    float tNear = max(max(t1.x, t1.y), t1.z);
    float tFar = min(min(t2.x, t2.y), t2.z);

    vec3 nNear = vec3(equal(t1,vec3(tNear)));
    vec3 nFar = vec3(equal(t2,vec3(tFar)));


    if(inAABB(box,r0)){
    	return surface_intersection(r0 + rd*length(rd*tFar), -nFar*sign(rd),length(rd*tFar),0);
    } else{
    	if(tNear>tFar || tNear < 1e-5){
        	return surface_intersection(r0,vec3(0),1e8,0);
    	} else{
            return surface_intersection(r0 + rd*length(rd*tNear),-nNear*sign(rd),length(rd*tNear),0);
        }
    }
}

surface_intersection ray_scene(vec3 r0, vec3 rd){
    surface_intersection intersect_final = ray_AABB(r0,rd,walls[0]);
    for(int i = 1; i < walls.length(); i++){
        surface_intersection intersect = ray_AABB(r0,rd,walls[i]);
        if(intersect.d<intersect_final.d){
            intersect_final = intersect;
        }
    }
    for(int i = 0; i < sunlights.length(); i++){
        surface_intersection intersect = ray_AABB(r0,rd,sunlights[i]);
        if(intersect.d<intersect_final.d){
            intersect_final = intersect;
            intersect_final.type = 1;
        }
    }
    for(int i = 0; i < lamplights.length(); i++){
        surface_intersection intersect = ray_AABB(r0,rd,lamplights[i]);
        if(intersect.d<intersect_final.d){
            intersect_final = intersect;
            intersect_final.type = 2;
        }
    }
    return intersect_final;
}
