void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col;
    float t = iTime*.5;
    vec2 uv = (.3)*(fragCoord*10.0-iResolution.xy)/iResolution.y;
    for(int c=0;c<3;c++){
        for(int i=0;i<9;i++)
        {
            float factor = 1.2;
            uv *= factor;
            //uv += sin(col.yx);
        	uv += float(i) + (sin(uv.x+uv.y)+cos(uv.y+t)+sin(t-uv.x));

            uv /= factor;
        }
     col[c] = (col[c] + sin(uv.x+uv.y))/2.0;
	}
    col*=2.0;
    fragColor = vec4(col,1.0);
}
