#define FIRST_COLOR vec4(1.0, 1.0, 0.0, 0.0)
#define SECOND_COLOR vec4(1.0, 0.0, 1.0, 0.0)

vec2 hash22(vec2 uv)
{
	return fract(sin(uv * mat2(35.482, 32.3871, 56.3861, 22.1762)) * 4341.0);
}

float hash12(vec2 uv)
{
	return fract(sin(dot(uv, vec2(32.482, 82.481))) * 4341.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Normalizacja współrzędnych ekranu
    vec2 uv = (fragCoord) / iResolution.y;// ([0-1], [0-1])

    uv *= 5.0;
    float pixelSize = fwidth(uv.x);
    uv.y -= iTime;

    vec2 rootUV = floor(uv);

   	float lifeTime = rootUV.y + hash12(rootUV + 0.3214) * 4.0;
    lifeTime += iTime - 1.3;
    lifeTime = 1.0 - lifeTime * 0.3;

    lifeTime = clamp(lifeTime, 0.0, 1.0);

    vec2 circleCenter = hash22(rootUV);
    circleCenter = (circleCenter - 0.5) * 0.6;

    uv = fract(uv);
    uv -= 0.5;

    circleCenter.x *= sin(iTime * hash12(rootUV + 0.2) * 5.0);

    uv -= circleCenter;

    float radius = 0.2;
    float blur = (1.0 - lifeTime) * 0.1 + pixelSize;
    float circle = 1.0 - smoothstep(radius - blur, radius + blur, length(uv));

    vec4 color = mix(SECOND_COLOR, FIRST_COLOR, lifeTime);

	fragColor = vec4(circle) * color * lifeTime;
}
