float blorb(vec2 uv, float threshold)
{
    uv -= 0.5;
    uv.y /= sqrt(uv.x + uv.y*iTime);
    uv.x += uv.y;
    float dist = length(uv);
    float c;
    if(dist<threshold) c = 1.;
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y += 0.32;
    uv.x += 0.005;
    fragColor = vec4(blorb(uv, sin(iTime*3.)-0.5),blorb(uv, sin(0.3*dot(iTime/3.,sin(iTime-0.9)))),blorb(uv, .2), 1.0);
}
