void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col;
    float t = iTime*.1;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y+vec2(t);
        for(int i=0;i<9;i++)
        {
            float factor = 1.5;
            vec2 uv1 = uv;
            uv /= factor;
            uv += uv1;
            uv += sin(uv.yx)/factor;
            uv *= factor;
            col += sin(uv.y-uv.x)+cos(uv.x-uv.y);
        }
    fragColor = vec4(vec3(col)*100.0,1.0);
}
