renderpass.code:
float brightness = 4.0;



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // fragCoord: 画面上のどこか. 画面上の座標 0,0 - 400, 300  入力値
    // iResolution: shadertoy が用意した便利変数. 画面サイズ (400, 300)

    // ピクセル座標から 0,0 - 1,1　のテクスチャ座標への変換
    vec2 uv = fragCoord.xy/iResolution.xy;

    if (fragCoord.x > iMouse.x)
    {
        fragColor = vec4(1, 0, 0, 1);
    }
    else
    {

    	vec4 col = texture(iChannel0, uv);
    	col.b = 1.0;

     	col *= brightness;

    	fragColor = col;
    }
}
