// Based on https://www.shadertoy.com/view/XdsBDH

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float fill  = texture(iChannel0, fragCoord.xy / iResolution.xy).r;
   	float edges = fwidth(fill);

    fragColor = vec4(fill, edges, 0., 1.);
}

// Based on https://www.shadertoy.com/view/XdsBDH

#define SEED 1234.
#define AUTO

#define T(u, v) texture(iChannel0, U / R + vec2(u, v) / R).x

void mainImage( out vec4 f, in vec2 U )
{
    vec2 R = iResolution.xy;

    float tL = T(-1,  1),
          tM = T( 0,  1),
          tR = T( 1,  1),
          mL = T(-1,  0),
          mM = T( 0,  0),
          mR = T( 1,  0),
          bL = T(-1, -1),
          bM = T( 0, -1),
          bR = T( 1, -1);

    // Hash 2 in 1 out
    vec3 p3 = fract(vec3(U.xyx + SEED) * 0.1031);
    p3 += dot(p3, p3.yzx + 19.19);
    float h = fract((p3.x + p3.y) * p3.z);

    float e = (tL + tM + tR + mL + mM + mR + bL + bM + bR) * float(h < 0.5);    

    if (mM == 1. || (tM == 1. && bL == 1.) || (tR == 1. && bM == 1.))
    {
        f = vec4(1.);
    }
    else
    {
        vec2 d = iMouse.xy - U;

#ifdef AUTO
    	f = vec4(iFrame < 1 && length(U / R - 0.5) < .005 || e == 1. || iMouse.z > 0. && dot(d, d) < 50.);
#else
        f = vec4(e == 1. || iMouse.z > 0. && dot(d, d) < 50.);
#endif
    }
}
