//noise function from iq: https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) 
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

//quintique
vec2 fade5(vec2 f) {return f * f * f * (f * (f * 6.0 - 15.0) + 10.0);}

//cubique
vec2 fade3(vec2 f) {return f*f*(3.0-2.0*f);}

float noise(vec2 p)
{
 	vec2 i = floor( p );
    vec2 f = fract( p );

    vec2 u = fade5(f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);

}


float fractal_noise(vec2 p)
{
    float f = 0.0;
	float amp = 1.;
    p = p*2.;
    for(int i = 0; i < 5 ; i++)
    {
     	f += amp/2.*noise(p);
        p = p*2.;
    }

    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 sky = vec3(0.5, 0.7, 0.8);
    vec3 col = vec3(0.0);

    vec3 cloud_color = vec3(1.0);

    float n = fractal_noise(fragCoord/(200.+iTime/1000.));
    col = mix( sky, cloud_color, smoothstep(-1., 40., n));
    col = mix( col, cloud_color, smoothstep(.75, -1.25, n));
    col = min(col, cloud_color);

    fragColor = vec4(col, 1.0);
}
