void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel = fragCoord / iResolution.xy;
    pixel.x -= 0.5;
    pixel.y -= 0.5;
    pixel.x *= iResolution.x / iResolution.y;

    float sizeBox = 0.25;
    float sizeLine = 0.015;
    float shape = step(abs(pixel.x+pixel.y), sizeBox);
    shape *= step(abs(pixel.y-pixel.x), sizeBox);
    shape *= step(sizeLine, length(pixel.x*cos(iTime)+pixel.y*sin(iTime)));
    shape *= step(sizeLine, length(pixel.y*-cos(iTime)+pixel.x*sin(iTime)));

