// Inspired by the work of John Harman: https://www.instagram.com/p/B9370OVHaju/ 

float hex(vec2 st) 
{
	st = abs(st);

    float h = dot(st, normalize(vec2(1.0,1.73)));
    h = max(h, st.x);

    return h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy - 0.5;
    float factor = iResolution.y / iResolution.x;

    st.y *= factor;

    float col = hex(st) * 25.0;
    float distScale = 1.0 + (length(hex(st)));

    col *= distScale * 2.0;
    col = 1.0 - step(fract(col - iTime),0.5 * distScale );

    fragColor = vec4(vec3(col),1.0);
}
