vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ){
        return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 st = fragCoord.xy*8./iResolution.y;
    vec3 color = vec3(.0);

    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    float m_dist = 1.;  // minimun distance
    for (int j= -1; j <= 1; j++ ) {
        for (int i= -1; i <= 1; i++ ) {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(i),float(j));

            // Random position from current + neighbor place in the grid
            vec2 offset = random2(i_st + neighbor);

            // Animate the offset
            offset = 0.5 + 0.5*sin(0.25*iTime + 6.2831*offset);

            // Position of the cell
            vec2 pos = neighbor + offset - f_st;

            // Cell distance
            float dist = length(pos);

            // Metaball it!
            m_dist = min(m_dist, m_dist*dist);
        }
    }


    color = pal(smoothstep(0.28,0.52,fract(m_dist*3.8)),
                vec3(0.900,0.760,0.725),vec3(0.780,0.517,0.434),
                vec3(cos(0.02*iTime)+0.497,0.915,sin(0.02*iTime)+0.027),
                vec3(sin(0.02*iTime)+0.410,cos(0.02*iTime)+0.108,0.265));

    fragColor = vec4(color,1.0);
}
