void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    float ifr = (iResolution.x*sin(float(iFrame)/60.))+fragCoord.y;

    // hold line if hit the edge
    if (ifr < 0.)
        ifr = 0.;

    // handle interlace odd / even lines
    int line = int(mod(uv.y,2.));
    if (line==0)
       ifr = -ifr;

    // Output to screen
    fragColor = texture(iChannel0,vec2(fragCoord.x+ifr,fragCoord.y)/iResolution.xy);
}
