void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 lu=vec2(0,1),ru= vec2(1,1),ll=vec2(0,0),rl=vec2(1,0);
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;
	if(iMouse.z>.5) col=texture(iChannel0,uv).xyz*step(.02,length(uv-lu))*step(.02,length(uv-ru))*step(.02,length(uv-ll))*step(.02,length(uv-rl));
	else col=texture(iChannel0,mix(mix(ll,rl,vec2(uv.x)),mix(lu,ru,vec2(uv.x)),vec2(uv.y))).xyz;

    fragColor = vec4(col,1.0);
}
