void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 wv = vec3(.0, .0, .0);
    for(float i = .0; i<1.;i+=1.)
    {
    	wv += vec3(.8*abs(11.*sin(uv.y))/(abs(uv.y + .27 - .1*(uv.x+0.5)*(sin(10.*uv.x +.3*iTime)*sin(iTime)+10. + 2.5*i*uv.x)*uv.x)*((1.*abs(1.*sin(3.1415*iTime)))+.5)*500.),
         			.0,
         			.8*abs(11.*sin(uv.y))/(abs(uv.y + .27 - .1*(uv.x+0.5)*(sin(5.*uv.x+0.1*iTime*sin(0.1*iTime))*sin(iTime)+10. + 2.5*i*uv.x)*uv.x)*((1.*abs(sin(1.618*iTime)))+.5)*500.));
    }
	vec3 bckgrnd = vec3(.2, 0., .3);
    vec3 col = wv + bckgrnd;
    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 mainSound( in int samp, float time )
{
    if(sin(time)>0. && sin(time)<1.)return vec2( sin(time * 1000.0), sin(time * 1000.0) );
    //if(sin(time)>0.5 && sin(time)<1.)return vec2( sin(time * 1000.0), sin(time * 1000.0) );
    if(sin(time)>-0.5 && sin(time)<0.)return vec2( sin(time * 900.0), sin(time * 900.0) );
    if(sin(time)>-1. && sin(time)<-0.5)return vec2( sin(time * 1200.0), sin(time * 1200.0) );

}
