float hash21(vec2 uv)
{
 	return uv.y * (0.3 * sin(uv.x * 700.125) + 0.2 * cos(uv.x * 695.125125) * sin(uv.x * 702.125125 + 0.3));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv.x *= 0.3;
    uv.x += iTime * 0.05;
    uv.y += cos(uv.x * 200. + 3.14 * 0.25) * 0.2 * smoothstep(0.0, 1.0, sin(uv.x * 100.0)) + 0.02 * hash21(uv);

   	vec3 col = vec3(0.1, 0.8, 0.3) * smoothstep(0.005, .0, distance(uv.y, 0.5));

    // Output to screen
    fragColor = vec4(col,1.0);
}
