vec2 rotate2D(vec2 _st, float _angle){
    _st =  mat2(cos(_angle),-sin(_angle),sin(_angle),cos(_angle)) * _st;
    return _st;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y;
    vec2 polar = vec2(length(uv), atan(uv.y / uv.x));   
    uv = rotate2D(uv, iTime);    
    vec3 bg = vec3(sin((abs(uv.x) + abs(uv.y)) * 2. - iTime), sin((abs(uv.x) + abs(uv.y)) *3.  - iTime), sin((abs(uv.x) + abs(uv.y)) *4.  - iTime));    
    float glowy = length(uv) * 2. * smoothstep(0.1, sin(length(vec2(polar.x, polar.y)) * 5. - iTime *2.), 0.3);        
    vec3 col = clamp(glowy * bg + bg * 0.5 + glowy * 0.5, -.4 ,1.);
    fragColor = vec4(col,1.0);
}
