// Shader:  SwampyStoneland.pde
//
// fork of '[SH17A] Jagged Landscape'
// https://www.shadertoy.com/view/4dSBRm

#define t0 texture(iChannel0,u).x
#define t1 texture(iChannel1,u*0.7)*0.1

vec4 T( vec2 u )
{
  u += iTime/vec2(48.95);
  vec4 t =         1.-t1;
  return 0.3-t*pow(1.-t0,3.) + t1;
}

void mainImage( out vec4 o, vec2 s )
{
  s = s/iResolution.y-vec2(.5,-.8);
  s /= 18. -s.y/.1;
  for ( float i=1.; i > 0.; i -= .002 )
    o = T( o.x<i ? s /= .9987 : s );
  o = exp2(-s.y*.3)*(o*9.5-T(s+.001).x*9.-.7)+.9; // fog
}
