float CENTER_X = 50.0f;
float CENTER_Y = 50.0f;
float RADIUS = 50.0f;

vec3 circle(vec2 fragCoord, vec2 uv) {
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
	float res = pow(fragCoord.x - iMouse.x,2.0f) + pow(fragCoord.y - iMouse.y,2.0f);
    float calcRadius = RADIUS + 5.0f * cos(iTime*5.0f);
    if (res < pow(calcRadius,2.0f)) return col;
    else return vec3(0.0,0.0,0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(circle(fragCoord, uv), 1.0f);
}
