void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy-.5)*2.;
    uv.y *= iResolution.y / iResolution.x;
	vec3 d = normalize(vec3(uv/2.,1));

    vec3 p = vec3(uv/2.,-100);
    float i = 0.;
    float e = 1.;

    float quasarscale = 1.;
    vec3 quasar = vec3(0,0,0);
    bool loc=false;
    vec3 lp;
    for (i=0.; i < 500.; i++)
    {
        float l = length(p);
        float bending = 5.*(1./pow(l,2.))*(.5+cos(iTime/2.)/2.);
        d=normalize(d-normalize(p)*bending);
        p += d/2.;
        if (p.z >= 100.)
        {
            e=1.;
            break;
        }

        float z = 1./(pow(p.y/2.,2.)+0.003)-abs(length(p.zx)-2.);

        quasar.x=z*float(z>0.);

        if (z>0.){
            loc=true;
            lp = p/20.;
        }

        if (length(p)<0.001 )
        {
         	e=0.;
            break;
        }

    }

    float scale=010.;
    float iscale =4000.;
    // Time varying pixel color
    //vec3 col =e* vec3(p.x/scale/2.+.5,p.y/scale/2.+.5,i/iscale);
	vec3 col = vec3(texture(iChannel0,vec3(p.x/scale/2.+.5,p.y/scale/2.+.5,p.z/scale/2.+.5)).rgb);
    // Output to screen
    if (loc)
    {
        float ang=iTime/(length(lp)+.5)/1.;
        quasar = texture(iChannel1,vec3(cos(ang)*lp.x-sin(ang)*lp.z,lp.y,sin(ang)*lp.x+cos(ang)*lp.z)).rgb*clamp(quasar.x,0.,0.7);
    }

    col += quasar/quasarscale;
    fragColor = vec4(e*col,1.0);
}
