void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
   // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float minDim = min(iResolution.x, iResolution.y);
    float rFinal =  minDim * 0.3;
    float rTime = mod(iTime * 0.9, 1.0);
    float r = rTime * rFinal;

    //float r2StartTime = step(0.5, rTime);

    float r2 = smoothstep(0.2, 1.0, rTime) * rFinal;

    vec2 xy = fragCoord - iResolution.xy * 0.5;

    float dist = xy.x * xy.x + xy.y * xy.y;
    float greaterThanR = step(r*r, dist);
    float greaterThanR2 = step(r2* r2, dist);

    vec3 col = vec3(1.0,1.0,1.0);

    col = col * (1.0- greaterThanR) * greaterThanR2;

	//col = vec3(uv, 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
