void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float slices = mod(floor(iTime), 100.);
    float base = 1.2;

    float arithmeticMean = 0.0;
    float geometricMean = 0.0;
    for(float i = 0.; i < slices; i++)
    {
        arithmeticMean += pow(base,i);
        geometricMean += log(pow(base,i));
    }
    arithmeticMean /= slices;
    geometricMean = exp(geometricMean/slices);
    float median = pow(base,slices/2.);

    for(float i = 0.; i < slices; i+=1.)
    {
        if(uv.y > 0.66)
        {
            if(uv.x >= i*1./slices && uv.x < i*1./slices+1./slices)
            {
                fragColor = vec4(pow(base,i)/arithmeticMean);
            }
        }
        else if(uv.y > 0.33)
        {
            if(uv.x >= i*1./slices && uv.x < i*1./slices+1./slices)
            {
                fragColor = vec4(pow(base,i)/geometricMean);
            }
        }
        else
        {
            if(uv.x >= i*1./slices && uv.x < i*1./slices+1./slices)
            {
                fragColor = vec4(pow(base,i)/median);
            }
        }
    }


}
