// I, Alex Evelyn, Am Releasing This Shader as Public Domain!!!

// This was modified from https://github.com/alex-evelyn/Sweet-Tea/blob/cf0fce1f6777ae117e782a324fda9f27dc3757c6/Scripts/Shaders/lgbt/transgender_flag.shader

// https://en.m.wikipedia.org/wiki/File:Transgender_Pride_flag.svg

// To Set Color Divide RGB values by 255. e.g white is rgb(255, 255, 255) -> divided (1, 1, 1)
vec3 blue = vec3(0.356862745098039, 0.807843137254902, 0.980392156862745); // Blue - #5BCEFA (91,206,250)
vec3 pink = vec3(0.96078431372549, 0.662745098039216, 0.72156862745098); // Pink - #F5A9B8 (245,169,184)
vec3 white = vec3(1, 1, 1); // White - #FFF (255,255,255)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UV Coordinates Come From Bottom Left (Up is Positive and Right is Positive. Also, the coordinates are from 0 to 1, so knock yourself out)
	// Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	vec4 screen = textureLod(iChannel0, uv, 0.0);

	// Shade In Color
	float v = dot(screen.xyz, vec3(0.33333, 0.33333, 0.33333));
	v = sqrt(v);

	bool set_blue = uv.y >= 0.8 || uv.y <= 0.2;
	bool set_white = uv.y > 0.4 && uv.y < 0.6;

	if(set_blue) {
        vec3 color = blue*v;
		fragColor = vec4(color,1.0);
	} else if(set_white) {
        vec3 color = white*v;
		fragColor = vec4(color,1.0);
	} else {
		vec3 color = pink*v;
		fragColor = vec4(color,1.0);
	} 
}
