float hash(vec2 p)
{
    return fract(sin(dot(p, vec2(27.754, 78.4476))) * 47125.3567);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y * 10.;
    uv.x += sin(iTime*3. + uv.y*0.3)*0.5;
    vec2 fluv = floor(uv);
    vec2 fruv = fract(uv) - 0.5;
    float t = fruv.y - fruv.x;
    if (hash(fluv) > 0.5) {t = fruv.y + fruv.x;}
    vec3 col = smoothstep(0.1,0.0,vec3(abs(t))) + smoothstep(0.9,1.0,vec3(abs(t)));
    col *= sin(abs(uv.y*0.1 + (iTime*3.)))*0.5+0.5;
    col *= 0.5 + 0.5*cos(iTime*5.+uv.yyy*0.2+vec3(0,2,4));
    fragColor = vec4(col,1.0);
}
