#define PI 3.1415926538

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Change to [-1, 1] instead.
    uv = uv * 2. - vec2(1.);

   	// Center in center is boring, let's shift it.
    vec2 center = vec2(0.125);

    // The difference between our position and the center.
    vec2 rv = uv - center;

    float angle = atan(rv.y, rv.x);
    angle += iTime * 2.;

    float period = PI/8.;
    float angleChoice = smoothstep(
        .25 * period,
        .75 * period,
        mod(angle, period));

    float radius = .125 + angleChoice * .75;

    vec3 bg = mix(
        vec3(.7, .8, .9),
        vec3(.9, .8, .7),
        angleChoice);
    vec3 fg = mix(
        vec3(.3, .2, .1),
        vec3(.1, .2, .3),
        angleChoice);

    //float lenChoice = smoothstep(
    vec3 col = 
        length(rv) < radius ? fg : bg;

    // Output to screen
    fragColor = vec4(col,1.0);
}
