float nrm(vec2 x, float p) {
    x = abs(x);
    float s = pow(pow(x.x,p)+pow(x.y,p),1./p);
    return s;
}
float norm_balls(vec2 x, float p) {
    return 2.*abs(mod(nrm(x*1.618,p)+iTime/2.,1.)-.5) - 1.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    uv *= 1.333;
    uv.x *= iResolution.x/iResolution.y;
    float p = iMouse.x/iResolution.x * 3.25+.75;
    if (iMouse.z<=0.)
        p = 1.;
    fragColor = vec4(.02/abs(norm_balls(uv,p*p)));
}
