//simplified based on FabriceNeyret2's comment

void mainImage( out vec4 O, vec2 u )
{
    vec2 res = iResolution.xy,
          uv = ( u* 2. - res) / min(res.x, res.y);

    float t = iTime,
          l = length(uv),
          r = l * exp2( t * .1),
          f = sin(r * r) * sin(t),
          g = uv.x / l;

    O = vec4( .1 / abs(f - g) );
}
