const vec2 ONES2 = vec2(1.,1.);
const vec3 ONES3 = vec3(1.,1.,1.);
const vec4 ONES4 = vec4(1.,1.,1.,1.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 transform1 = uv * .02 + iTime * .01 * ONES2;

    vec2 transform2 = uv * .05 - iTime * .01 * ONES2;

    // Time varying pixel color
    vec3 col1 = vec3(texture(iChannel0, transform1).x * ONES3);
    vec3 col2 = vec3(texture(iChannel0, transform2).x * ONES3);

    vec3 col = col1 * (ONES3 - col2);
    // Output to screen
    fragColor = vec4(col, col);
}
