#define t iTime
#define s(a) d += 1./length(p+vec2(sin(a), cos(a)))

void mainImage(out vec4 c, in vec2 f )
{
    vec2 p = (f-iResolution.xy/2.)/iResolution.y*4.;

    float d, i;
    for (s(t);i<6.;i+=1.256)
        s(i-t);

    d = d*.75-6.;

    // free antialiasing by FabriceNeyret2
    c = vec4(d/min(1.,fwidth(d)) );
}
