#define MAX_STEPS 100.

float remap01(float a, float b, float value) {
	return (value - a)/(b - a);    
}

vec3 walkInRd(vec3 ro, vec3 rd, float t) { 
	return ro + rd * t;    
}

float sphereDist(vec3 ro, vec3 rd, vec3 s) {
	vec3 ros = s-ro;
    float scalar = dot(rd, ros);
    vec3 p = walkInRd(ro, rd, scalar);

    return length(s-p) - 0.1; 
}

// (k-p).n = 0 <> n.k = n.p <> n.(ro + rd*t) = n.p <> n.rd*t = n.p - n.ro <> t = (n.p - n.ro) / n.rd;
float planeDist(vec3 ro, vec3 rd, vec3 planePoint, vec3 normal) {
    planePoint = planePoint-ro;
    normal = normalize(normal);

    float t = (dot(planePoint, normal)-dot(ro, normal)) / dot(rd, normal);

    return t < 0. ? MAX_STEPS + 1. : t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(0. , 0., 0.);
    vec3 rd = normalize(vec3(uv, 1));


 	float d = planeDist(ro, rd, vec3(0, -1.,0), vec3(cos(iTime), 1., 0.));
    col += step(remap01(0., MAX_STEPS, d), 1.);
    fragColor = vec4(col,1.0);
}
