void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord - (iResolution.xy / vec2(2.0));
    // coord *= iTime / 10.0;
    int x = int(coord.x);
    int y = int(coord.y);

    int a = x*x + y*y;

    // WARNING: flashing
    // a += int(iTime * 1000.0);
    // a -= int(iTime * 1000.0);

    // emulating JS: a.toString(16) + "0".repeat(6 - a.toString(16).length)
    if (a < 0x100000) a = a << 4;
    if (a < 0x100000) a = a << 4;
    if (a < 0x100000) a = a << 4;
    if (a < 0x100000) a = a << 4;
    if (a < 0x100000) a = a << 4;

    fragColor = vec4(
        float((a >> 16) & 255) / 255.0,
        float((a >>  8) & 255) / 255.0,
        float((a >>  0) & 255) / 255.0,
        1.0
    );
}
