vec3 aa(vec2 p){
    float pixelThird = (1./iResolution.x) / 3.0;
    vec2 aa = vec2(-pixelThird, pixelThird);
    vec3 c1 = texture(iChannel0, p+aa.xx).rgb;
    vec3 c2 = texture(iChannel0, p+aa.xy).rgb;
    vec3 c3 = texture(iChannel0, p+aa.yx).rgb;
    vec3 c4 = texture(iChannel0, p+aa.yy).rgb;
    return (c1+c2+c3+c4) / 4.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 cv = (fragCoord.xy - .5* iResolution.xy) / iResolution.y;
    float t = iTime*.1;
    float a = atan(cv.y, cv.x);
    float d = .25*sin(length(cv*8.)-t*2.)+t*.5;
    vec2 p = vec2(d, cos(2.*a));
    vec3 col = aa(p);
    fragColor = vec4(col,1.0);
}
