void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float i = float(iFrame);
    float a = i / (100. + 100. * (sin(iTime * 0.5)*.5+.5));

   	fragColor = texture(iChannel0, (fragCoord+vec2(cos(a),sin(a)))/iResolution.xy);

    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.y;

    uv = mod(uv, 0.8)-0.4;

    float m = 2./iResolution.y/dot(uv,uv);


    fragColor = fragColor * 0.999 + vec4(m) * 0.001;
}
