const vec4 Bgnd = vec4(vec3(.8), 1);
const vec2 Size = vec2(200, 200);

/**
 * @param uv coord of a pixel, the sprite is drawn between 0. and 1 but the 
 *     coordinates are bigger
 * @return the color of the pixel
 */
vec4 drawSprite(vec2 uv) {
    float t = mod(.5 * iTime, 1.2) - .1;
    float offset = smoothstep(t, t + .15, 1. - uv.y) - 1.;
    uv.x += .4 * -pow(offset, 5.);

    vec4 tex = vec4(0);
    if(all(greaterThan(step(abs(uv - .5), vec2(.5)), vec2(.5))))
    	tex = texture(iChannel0, vec2(uv.x / 6.6, uv.y));

    tex.a *= smoothstep(t - .01, t + .1, 1. - uv.y);
    return tex;
}


void addTexture(inout vec4 col, in vec4 sprite) {
    col = mix(col, vec4(sprite.rgb, 1.), sprite.a);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - (iResolution.xy - Size) / 2.) / Size;
    vec4 col = Bgnd;
    addTexture(col, drawSprite(uv));

    fragColor = col;
}
