void disk(vec2 r, vec2 center, float radius, vec3 color, inout vec3 pixel)
{
	if(length(r-center) < radius)
    {
    	pixel = color;   
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Convert coordinates to uniform centered system
	vec2 uv = 2.0 * vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    vec3 bgColor = vec3(0.5);
    vec3 col1 = vec3(0);



    float fft = texture(iChannel0, vec2(abs(uv.y*uv.x), 0.075)).x;
    float wave = texture(iChannel0, vec2(uv.x, .0075)).x;
    vec3 col = vec3( fft, 4.0*fft*(1.0-fft), 1.0-fft ) * fft;

    col += 1.0 - smoothstep(0.0,0.15, abs(wave - uv.y));
    //fragColor = vec4(col, 1.0);

    vec3 pixel = vec3(fft, fft, fft);

    disk(uv, vec2(0,0), fft, col1, pixel);

    fragColor = -0.2f + vec4(pixel, 1.0);
}
