vec3 rgb(float r, float g, float b) 
{
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

vec3 circle(vec2 uv, vec2 pos, float rad) 
{

	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);

	return vec3(1.0 - t);
}

float variation(vec2 v1, vec2 v2, float strength, float speed) 
{
	return sin(
        dot(normalize(v1), normalize(v2)) * strength + iTime * speed
    ) / 100.0;
}

vec3 paintCircle (vec2 uv, vec2 center, float rad, float width) 
{

    vec2 diff = center-uv;
    float len = length(diff);

    len += variation(diff, vec2(0.0, 1.0), 5.0, 2.0);
    len -= variation(diff, vec2(1.0, 0.0), 5.0, 2.0);

    float circle = smoothstep(rad-width, rad, len) - smoothstep(rad, rad+width, len);
    return vec3(circle);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;


    vec2 center = iResolution.xy * 0.5;
	float radius = .3 * iResolution.y;


	vec3 actualCircle = paintCircle(uv, center, radius, 0.1);
    vec3 anotherCircle =circle(uv, center, .1 * iResolution.y);
    vec3 anotherCircle2 =paintCircle(uv, center, .2 * iResolution.y,0.1);
    vec3 anotherCircle3 =paintCircle(uv, center, .4 * iResolution.y,0.1);
    vec3 anotherCircle4 =paintCircle(uv, center, .5 * iResolution.y,0.1);

    vec2 v = rotate2d(iTime) * uv;

    actualCircle *= vec3(v.x, v.y, 0.7-v.y*v.x);
	actualCircle += paintCircle(uv, center, radius, 0.01);


    anotherCircle *= vec3(v.x, v.y, 0.7-v.y*v.x);
	anotherCircle += paintCircle(uv, center, .1 * iResolution.y, 0.01);

    anotherCircle2 *= vec3(v.x, v.y, 0.7-v.y*v.x);
	anotherCircle2 += paintCircle(uv, center, .2 * iResolution.y, 0.01);

    anotherCircle3 *= vec3(v.x, v.y, 0.7-v.y*v.x);
	anotherCircle3 += paintCircle(uv, center, .4 * iResolution.y, 0.01);

    anotherCircle4 *= vec3(v.x, v.y, 0.7-v.y*v.x);
	anotherCircle4 += paintCircle(uv, center, .5 * iResolution.y, 0.01);

    actualCircle += anotherCircle;
    actualCircle += anotherCircle2;
    actualCircle += anotherCircle3;
    actualCircle += anotherCircle4;


	fragColor = vec4(actualCircle,1.);
}
