float random(vec2 co){
    return fract(sin(dot(co.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float amp = 0.7 * random(uv / 111112.0); //振幅
    float amp2 = 0.2; 
    float freq = 17.0; //周波数
    float freq2 = 43.0;
    float thickness = 0.02 / 2.0; //線の太さ
    float smoothing = 4.0 / iResolution.x; //画面解像度に応じたアンチエイリアス

    //wave1
    float post = uv.x * freq + iTime * 4.0;
    float wave = amp * sin(post);
    //wave2
    float post2 = uv.x * freq2 + iTime;
    float wave2 = amp2 * sin(floor(post2)) * 1.0;


    //サインカーブをtimeでmix
    float y = mix(wave, wave2, (sin(iTime) + 1.0) / 2.0);

    vec3 linecolor = 0.5 + vec3(1.0 * (sin(iTime) + 1.0) / 2.0, 1.0 * (cos(iTime) + 1.0) / 2.0, 1.9);
    vec3 coltop = vec3(smoothstep(0.0, smoothing, uv.y - y - 0.5 + thickness)) * linecolor;
    vec3 colbottom = vec3(1. - smoothstep(0.0, smoothing, uv.y - y - 0.5 - thickness));

	vec3 col = coltop * colbottom;

    fragColor = vec4(col,1.0);
}
