vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) 
{
	float d = length((pos) - uv) - rad;
	float t = clamp(d, 0.0, 1.0);

	return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;

	vec2 center = iResolution.xy * 0.5;
	float radius = 0.25 * iResolution.y;


    // Background layer
	vec4 bg = vec4(vec3(1.0, 1.0, 1.0), 1.0);

	// Circle
	vec3 orange = vec3(1.0, 0.5, 0.3);
	vec4 circle = circle(uv, center , radius, orange);

     uv = fragCoord.xy / iResolution.xy;


    //x bars
    float valx = uv.x;
    valx = fract(sin(abs(uv.x * 3.0)));
    vec3 mCol2 = vec3(sin(valx * iTime * 4.0) * 0.5,
                     sin(valx * iTime * 8.0) * 0.8, 
                     (sin(valx * iTime) * 6.0) + 0.0);

    //y bars
    float valy = uv.y;
    valy = fract(sin(abs(uv.y * 3.0)));
    vec3 mCol3 = vec3(abs(sin(valy * iTime * 4.0)) * 0.5,
                     abs(sin(valy * iTime * 8.0)) * 0.1, 
                     (abs(sin(valy * iTime) * 6.0)) + 0.0);
	// Blend the two
	fragColor = mix(bg, circle, circle.a) + vec4(mCol2 + mCol3, 1.0);

}

