void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float amp = 20.;
    float off = .1;

    vec2 uv = fragCoord/iResolution.xy;
    vec3 blurcol = vec3(sin(amp*(cos(iTime)+uv.y)),
                sin(amp*(cos(iTime + off)+uv.y)),    
		    	sin(amp*(cos(iTime + 2. * off)+uv.y)));

    vec3 col = vec3(blurcol.r, 0., 0.);
    if(col.r < 0.1){
        col.r = 0.0;
        col.g = sin(blurcol.g);
       	if(col.g < 0.1){
        	col.g = 0.0;
        	col.b = sin(blurcol.b);
    	}
    }

    col = mix(col, blurcol, step(0., -cos(iTime / 2.)));
    fragColor = vec4(sqrt(col),1.0);
}
