void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    float d = length(uv);

    float radius = .3;

    float brightness = 1.;
   	vec3 halo = brightness * vec3(radius / d);

    halo  = mix(halo, vec3(.0, .0, .0), d);

    halo *= vec3(smoothstep(radius, radius +.01, d));

    vec3 color = vec3(1., .0, .0);

    fragColor = vec4(halo * color, 1.);

}
